/* Procedure structure for procedure `case_01_login` */

/*!50003 DROP PROCEDURE IF EXISTS  `case_01_login` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `case_01_login`(IN surel VARCHAR(255), IN kata_sandi VARCHAR(255))
BEGIN
		DECLARE valid INT;
		DECLARE pass_protect VARCHAR(255);
		DECLARE role VARCHAR(255);
		
		SET pass_protect = (SHA1(kata_sandi));
		SET valid = (SELECT `tb_pengguna`.`id_pengguna` FROM tb_pengguna WHERE tb_pengguna.`alamat_surel`= surel AND tb_pengguna.`kata_sandi`= pass_protect);
		SET role = (SELECT tb_pengguna.`role` FROM tb_pengguna WHERE tb_pengguna.`alamat_surel`= surel AND tb_pengguna.`kata_sandi`= pass_protect);
		
		IF (valid>0) THEN 
		    IF (role = 'Admin') THEN
			SELECT `tb_pengguna`.`id_pengguna` AS 'id'FROM tb_pengguna WHERE tb_pengguna.`alamat_surel`= surel AND tb_pengguna.`kata_sandi`= pass_protect;
		    END IF;
		    if (role = 'Pengguna') then 
			SELECT `tb_pengguna`.`id_pengguna` AS 'id'FROM tb_pengguna WHERE tb_pengguna.`alamat_surel`= surel AND tb_pengguna.`kata_sandi`= pass_protect;
		    end if;
